import React, { Component } from "react";
import { connect } from "react-redux";
import { handleAddAnswer } from "../actions/questions";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  FormGroup,
  Label,
  Input,
  Form,
  Button,
  Row,
  Col,
} from "reactstrap";

class QuestionSolution extends Component {
  state = {
    answer: "",
  };
  handleSelectAnswer = (e) => {
    const text = e.target.value;
    this.setState(() => ({
      answer: text,
    }));
  };
  /*
  <div>
        <select onChange={this.handleSelectAnswer}>
          <option defaultValue value="optionOne">
            {question.optionOne.text}
          </option>
          <option value="optionTwo">{question.optionTwo.text}</option>
        </select>
        <button type="submit" onClick={this.submitAnswer}>
          Submit answer
        </button>
      </div>
      */
  submitAnswer = (e) => {
    e.preventDefault();
    const { answer } = this.state;
    if (answer === "") {
      this.setState(() => ({
        answer: "optionOne",
      }));
    }
    this.props.saveQuestionAnswer(this.state.answer);
  };
  render() {
    const { question } = this.props;
    const { answer } = this.state;
    console.log(question);
    return (
      <Row>
        <Col sm="12" md={{ size: 6, offset: 3 }}>
          <Card>
            <CardHeader>
              <User id={question.author.id} />
            </CardHeader>
            <CardBody>
              <CardTitle>Would You Rather</CardTitle>
              {answer ? (
                <div>
                  <FormGroup>
                    <FormGroup check disabled>
                      <Label check>
                        <Input
                          type="radio"
                          checked={answer === "optionOne"}
                          readOnly
                        />{" "}
                        {question.optionOne.text}
                      </Label>
                    </FormGroup>
                    <FormGroup check disabled>
                      <Label check>
                        <Input
                          type="radio"
                          checked={answer === "optionTwo"}
                          readOnly
                        />{" "}
                        {question.optionTwo.text}
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </div>
              ) : (
                <Form onSubmit={this.submitAnswer}>
                  <FormGroup tag="fieldset">
                    <FormGroup>
                      <Label>
                        <Input
                          type="radio"
                          name="radio1"
                          value="optionOne"
                          onChange={this.handleSelectAnswer}
                        />
                        {question.optionOne.text}
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Label>
                        <Input
                          type="radio"
                          name="radio2"
                          value="optionTwo"
                          onChange={this.handleSelectAnswer}
                        />
                        {question.optionTwo.text}
                      </Label>
                    </FormGroup>
                  </FormGroup>
                  <Button disabled={selectedOption === ""}>Submit</Button>
                </Form>
              )}
            </CardBody>
          </Card>
        </Col>
      </Row>
    );
  }
}

function mapStateToProps({ questions }, props) {
  const { qid } = props.match.params;
  const question = questions[qid];
  return { question };
}

function mapDispatchToProps(dispatch, props) {
  const { qid } = props.match.params;

  return {
    saveQuestionAnswer: (answer) => {
      dispatch(handleAddAnswer(qid, answer));
    },
  };
}
export default connect(mapStateToProps, mapDispatchToProps)(QuestionSolution);
