import React, { Component } from "react";
import { connect } from "react-redux";
class Leaderboard extends Component {
  render() {
    const { users } = this.props;
    return (
      <div>
        <ul>
          {Object.keys(users)
            .map((user) => (
              <li key={user}>
                <h3>{users[user].name}</h3>
                <img src={users[user].avatarURL} />
                <h3>
                  Total score:{" "}
                  {users[user].questions.length + users[user].answers.length}
                </h3>
                <h3>Questions: {users[user].questions.length}</h3>
                <h3>Answers: {users[user].answers.length}</h3>
              </li>
            ))
            .sort()}
        </ul>
      </div>
    );
  }
}

function mapStateToProps({ users }) {
  return { users };
}

export default connect(mapStateToProps)(Leaderboard);
