import { RECEIVE_USERS } from "../actions/users";
import { ADD_QUESTION } from "../actions/questions";
export default function users(state = {}, action) {
  switch (action.type) {
    case RECEIVE_USERS:
      return {
        ...state,
        ...action.users,
      };
    case ADD_QUESTION:
      const user = action.question.author;
      return {
        ...state,
        [user]: {
          ...state[user],
          [action.question.id]: action.question,
        },
      };
    default:
      return state;
  }
}
