import React, { Component } from "react";
import { connect } from "react-redux";
class Leaderboard extends Component {
  render() {
    const { users, orderedUsers } = this.props;
    return (
      <div>
        <ul>
          {orderedUsers.map((user) => (
            <li key={user}>
              <h3>{users[user].name}</h3>
              <img src={users[user].avatarURL} />
              <h3>
                Total score:
                {users[user].questions.length +
                  Object.keys(users[user].answers).length}
              </h3>
              <h3>Questions: {users[user].questions.length}</h3>
              <h3>Answers: {Object.keys(users[user].answers).length}</h3>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

function mapStateToProps({ users }) {
  const orderedUsers = Object.keys(users).sort(
    (a, b) =>
      users[b].questions.length +
      Object.keys(users[b].answers).length -
      (users[a].questions.length + Object.keys(users[a].answers).length)
  );
  return { orderedUsers, users };
}

export default connect(mapStateToProps)(Leaderboard);
