import {
  RECEIVE_QUESTIONS,
  ADD_QUESTION,
  ADD_ANSWER,
} from "../actions/questions";

export default function questions(state = {}, action) {
  switch (action.type) {
    case RECEIVE_QUESTIONS:
      return {
        ...state,
        ...action.questions,
      };
    case ADD_QUESTION:
      const { question } = action;
      return {
        ...state,
        [action.question.id]: question,
      };
    case ADD_ANSWER:
      const { answer, authedUser } = action; // add question to the const if needed
      return {
        ...state,
        [action.question.id]: {
          ...state[action.question.id],
          [answer]: {
            ...state[action.question.id][answer],
            votes: state[action.question.id][answer].votes.concat([
              authedUser.id,
            ]),
          },
        },
      };
    default:
      return state;
  }
}
