import React, { Component } from "react";
import { connect } from "react-redux";
import { handleAddAnswer } from "../actions/questions";
class QuestionSolution extends Component {
  state = {
    answer: "",
  };
  handleSelectAnswer = (e) => {
    const text = e.target.value;
    this.setState(() => ({
      answer: text,
    }));
  };
  submitAnswer = (e) => {
    e.preventDefault();
    const { answer } = this.state;
    const { addAnswerToQuestion, user, question, dispatch } = this.props;
    dispatch(
      handleAddAnswer({
        question,
        answer,
        authedUser: user,
      })
    );
    this.setState(() => ({
      answer: "",
    }));
  };
  render() {
    const { user, question } = this.props;

    console.log(question);
    return (
      <div>
        <select onChange={this.handleSelectAnswer}>
          <option value="optionOne">{question.optionOne.text}</option>
          <option value="optionTwo">{question.optionTwo.text}</option>
        </select>
        <button type="submit" onClick={this.submitAnswer}>
          Submit answer
        </button>
      </div>
    );
  }
}

function mapStateToProps({ questions, users, authedUser }, props) {
  const { questionId } = props.match.params;
  const question = questions[questionId];
  const user = users[authedUser];
  return { user, question };
}

export default connect(mapStateToProps)(QuestionSolution);
