import React, { Component, Fragment } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { connect } from "react-redux";
import { handleInitialData } from "../actions/shared";
import LoadingBar from "react-redux-loading";
import Nav from "./Nav";
import Login from "./login";
import Dashboard from "./Dashboard";
// adding Loading bar inside the container is not right, so it should be under router but that will lead to having to render two things under Router, so we could add a div but that's too excessive so we add a fragment
class App extends Component {
  state = {
    userId: "",
  };
  componentDidMount() {
    this.props.dispatch(handleInitialData());
  }
  render() {
    const user = this.props.authedUser.length > 0 ? this.props.authedUser : "";
    const { notAuthorized } = this.props;
    return (
      <Router>
        <Fragment>
          <LoadingBar />
          <div className="container">
            {this.props.loading === true ? null : (
              <div>
                <NavBar />
                <Routes notLoggedIn={notLoggedIn} />
                <Route path="/login" exact component={Login} />
                {user !== "" && (
                  <Fragment>
                    <Nav />
                    <Route path="/x" exact component={Dashboard} />
                  </Fragment>
                )}
              </div>
            )}
          </div>
        </Fragment>
      </Router>
    );
  }
}

function mapStateToProps({ authedUser }) {
  return {
    loading: authedUser === null,
    notAuthorized: authedUser === null,
  };
}

export default connect(mapStateToProps)(App);

/*
connect([mapStateToProps], [mapDispatchToProps], [mergeProps], [options])
mapStateToProps - If this argument is specified,
 the new component will subscribe to Redux store updates. 
 This means that any time the store is updated, 
 mapStateToProps will be called. The results of mapStateToProps 
 must be a plain object, which will be merged into the component’s
  props. If you don't want to subscribe to store updates, pass null 
  or undefined in place of mapStateToProps.

mapDispatchToProps - If an object is passed, 
each function inside it is assumed to be a Redux action creator. 
An object with the same function names,
 but with every action creator wrapped into a dispatch call 
 so they may be invoked directly, will be merged into the component’s 
 props. If a function is passed, it will be given dispatch as the first
 parameter. It’s up to you to return an object that somehow uses
  dispatch to bind action creators in your own way.
   (Tip: you may use the bindActionCreators() helper from Redux.)

   */
