import {
  RECEIVE_QUESTIONS,
  ADD_QUESTION,
  ADD_ANSWER,
} from "../actions/questions";

export default function questions(state = {}, action) {
  switch (action.type) {
    case RECEIVE_QUESTIONS:
      return {
        ...state,
        ...action.questions,
      };
    case ADD_QUESTION:
      const { question } = action;
      return {
        ...state,
        [action.question.id]: question,
      };
    case ADD_ANSWER:
      const { answer, authedUser } = action;
      return {
        ...state,
        [question.id]: {
          ...state[question.id],
          [answer]: {
            ...state[question.id].answer,
            votes: state[question.id].answer.votes.concat([authedUser.id]),
          },
        },
      };
    default:
      return state;
  }
}
