import React, { Component } from "react";
import { connect } from "react-redux";
import { handleAddAnswer } from "../actions/questions";
class QuestionSolution extends Component {
  state = {
    answer: "",
  };
  handleSelectAnswer = (e) => {
    const text = e.target.value;
    this.setState(() => ({
      answer: text,
    }));
  };
  submitAnswer = (e) => {
    e.preventDefault();
    const { answer } = this.state;
    const { addAnswerToQuestion, user, questionId, questions } = this.props;
    const question = questions[questionId];
    addAnswerToQuestion(question, answer, user);
    this.setState(() => ({
      answer: "",
    }));
  };
  render() {
    const { user, questionId, questions } = this.props;
    const question = questions[questionId];

    console.log(questionId);
    return (
      <div>
        <select onChange={this.handleSelectAnswer}>
          <option value="optionOne">{question.optionOne.text}</option>
          <option value="optionTwo">{question.optionTwo.text}</option>
        </select>
        <button type="submit" onClick={this.submitAnswer}></button>
      </div>
    );
  }
}

function mapStateToProps({ questions, users, authedUser }) {
  const user = users[authedUser];
  return { user, questions };
}

function mapDispatchToProps(dispatch) {
  return {
    addAnswerToQuestion: (question, answer, authedUser) => {
      handleAddAnswer(question, answer, authedUser);
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(QuestionSolution);
