import React, { Component } from "react";
import { connect } from "react-redux";
import Question from "./Question";

class Dashboard extends Component {
  state = {
    selection: "unanswered",
  };
  handleSelection = (e) => {
    e.preventChange();
    const text = e.target.value;
    this.setState(() => ({
      selection: text,
    }));
  };
  render() {
    const { selection } = this.state;
    const { user, questions } = this.props;
    return (
      <div>
        <select onChange={this.handleSelection}>
          <option selected value="unanswered">
            Unanswered
          </option>
          <option value="answered">answered</option>
        </select>
        <div className="selected-option">
          <ul>
            {selection === "answered"
              ? Object.keys(user.answers).map((id) => (
                  <Question selection={selection} />
                ))
              : Object.keys(questions)
                  .filter((id) => Object.keys(user.answers[id]).length === 0)
                  .map((id) => <Question selection={selection} />)}
          </ul>
        </div>
      </div>
    );
  }
}

function mapStateToProps({ questions, users, authedUser }) {
  const user = users[authedUser];

  return { questions, user };
}
export default connect(mapStateToProps)(Dashboard);
